import React, { useState, useEffect, useCallback } from 'react';
import Calendar from './Calendar';
import { CalendarIcon, X } from 'lucide-react';
import axios from 'axios';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);
  const [isSaving, setIsSaving] = useState(false);
  const [message, setMessage] = useState('');

  // Fetch existing dates when component mounts
  useEffect(() => {
    fetchBusyDates();
  }, []);

  const fetchBusyDates = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/admin/busy-dates');
      // Fix timezone issue by adjusting the date
      const adjustedDates = response.data.dates.map(dateObj => {
        const date = new Date(dateObj.date);
        // Remove timezone offset
        date.setMinutes(date.getMinutes() + date.getTimezoneOffset());
        return date;
      });
      setSelectedDates(adjustedDates);
    } catch (error) {
      setMessage('Error fetching dates');
      console.error('Error:', error);
    }
  };

  const handleSelectDate = (date) => {
    // Toggle date selection
    setSelectedDates(prev => {
      const dateStr = new Date(date).toISOString().split('T')[0];
      const exists = prev.some(d => 
        new Date(d).toISOString().split('T')[0] === dateStr
      );

      if (isNaN(new Date(dateStr).getTime())) {
        return prev;
      }
      
      if (exists) {
        return prev.filter(d => 
          new Date(d).toISOString().split('T')[0] !== dateStr
        );
      } else {
        return [...prev, new Date(date)];
      }
    });
  };

  const handleRemoveDate = (dateToRemove) => {
    setSelectedDates(prev => 
      prev.filter(date => 
        date.toISOString().split('T')[0] !== dateToRemove.toISOString().split('T')[0]
      )
    );
  };

  const handleSaveDates = async () => {
    setIsSaving(true);
    setMessage('');

    try {
      const formattedDates = selectedDates.map(date => {
        const adjustedDate = new Date(date);
        // Format as YYYY-MM-DD
        return adjustedDate.toISOString().split('T')[0];
      });

      await axios.post('http://localhost:3000/api/admin/busy-dates', {
        dates: formattedDates
      });
      setMessage('Dates saved successfully!');
      // Refresh the dates after saving
      await fetchBusyDates();
    } catch (error) {
      setMessage('Error saving dates');
      console.error('Error:', error);
    } finally {
      setIsSaving(false);
    }
  };

  const formattedDates = selectedDates.map(date => {
    const adjustedDate = new Date(date);
    // Add additional validation to ensure date is valid
    if (isNaN(adjustedDate.getTime())) {
      return null;
    }
    // Format as YYYY-MM-DD
    return adjustedDate.toISOString().split('T')[0];
  }).filter(date => date !== null);

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
      <div>
      <Calendar
  selectedDates={selectedDates.length > 0 ? selectedDates : []}
  onSelectDates={handleSelectDate}
  className="mb-4"
/>
        
        {selectedDates.length > 0 && (
          <div className="mt-6 p-4 bg-blue-50 rounded-md border border-blue-200">
            <h3 className="text-lg font-semibold text-blue-800 mb-2">
              Selected Dates ({selectedDates.length}):
            </h3>
            <ul className="space-y-1">
              {selectedDates
                .sort((a, b) => new Date(a) - new Date(b))
                .map((date, index) => (
                  <li 
                    key={date.toString() + index} 
                    className="flex items-center justify-between text-blue-700 p-2 hover:bg-blue-100 rounded-md"
                  >
                    <div className="flex items-center">
                      <CalendarIcon className="mr-2" size={16} />
                      {formatDisplayDate(date)}
                    </div>
                    <button
                      onClick={() => handleRemoveDate(date)}
                      className="text-red-500 hover:text-red-700 p-1"
                      title="Remove date"
                    >
                      <X size={16} />
                    </button>
                  </li>
              ))}
            </ul>
            <div className="mt-4 flex justify-between items-center">
              <button
                onClick={handleSaveDates}
                disabled={isSaving}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300"
              >
                {isSaving ? 'Saving...' : 'Save Dates'}
              </button>
              {selectedDates.length > 0 && (
                <button
                  onClick={() => setSelectedDates([])}
                  className="px-4 py-2 text-red-600 hover:text-red-700"
                >
                  Clear All
                </button>
              )}
            </div>
            {(message || '').includes('Error') ? (
          <p className="mt-2 text-red-600">{message || 'No error message'}</p>
        ) : (
          <p className="mt-2 text-green-600">{message || 'No message'}</p>
        )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminCalendar;
