import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Bell, Settings, LogOut, User, Shield, Users, Activity, 
  FileText, X, Moon, Sun, Globe, Lock, BellRing, 
  Mail, Toggle, AlertTriangle 
} from 'lucide-react';
import { Alert, AlertTitle, AlertDescription } from "@/components/ui/alert";
import { Switch } from "@/components/ui/switch";

const AdminSidebar = ({ isOpen, onClose }) => {
  const navigate = useNavigate();
  const [activeSection, setActiveSection] = useState('dashboard');
  const [showNotifications, setShowNotifications] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [stats, setStats] = useState({
    totalForms: 0,
    totalSubmissions: 0,
    activeUsers: 0
  });
  
  // Settings State
  const [settings, setSettings] = useState({
    darkMode: false,
    emailNotifications: true,
    pushNotifications: true,
    language: 'english',
    autoLogout: 30, // minutes
    twoFactorAuth: false
  });

  // Simulated data fetching
  useEffect(() => {
    // Fetch notifications (simulated)
    const fetchNotifications = async () => {
      // In real app, this would be an API call
      const mockNotifications = [
        { 
          id: 1, 
          message: "New form submission - Visitor's Form", 
          time: "5 min ago",
          type: 'submission',
          read: false
        },
        { 
          id: 2, 
          message: "New Science Fair application", 
          time: "1 hour ago",
          type: 'application',
          read: false
        },
        { 
          id: 3, 
          message: "System maintenance scheduled", 
          time: "2 hours ago",
          type: 'system',
          read: true
        }
      ];
      setNotifications(mockNotifications);
    };

    // Fetch stats (simulated)
    const fetchStats = async () => {
      // In real app, this would be an API call
      setStats({
        totalForms: 4,
        totalSubmissions: 156,
        activeUsers: 23
      });
    };

    fetchNotifications();
    fetchStats();
  }, []);

  const markNotificationAsRead = (id) => {
    setNotifications(notifications.map(notif => 
      notif.id === id ? { ...notif, read: true } : notif
    ));
  };

  const handleSettingChange = (key, value) => {
    setSettings(prev => ({
      ...prev,
      [key]: value
    }));

    // In real app, save to backend
    console.log(`Setting ${key} changed to ${value}`);
  };

  const getNotificationIcon = (type) => {
    switch(type) {
      case 'submission': return FileText;
      case 'application': return Mail;
      case 'system': return AlertTriangle;
      default: return Bell;
    }
  };

  const handleSignOut = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const renderDashboard = () => (
    <div className="space-y-6">
      {/* Quick Stats */}
      <div className="grid grid-cols-1 gap-4">
        <div className="bg-gray-50 p-4 rounded-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-500">Total Forms</p>
              <p className="text-xl font-semibold text-gray-800">{stats.totalForms}</p>
            </div>
            <FileText className="w-5 h-5 text-blue-600" />
          </div>
        </div>
        <div className="bg-gray-50 p-4 rounded-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-500">Total Submissions</p>
              <p className="text-xl font-semibold text-gray-800">{stats.totalSubmissions}</p>
            </div>
            <Activity className="w-5 h-5 text-blue-600" />
          </div>
        </div>
        <div className="bg-gray-50 p-4 rounded-lg">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-500">Active Users</p>
              <p className="text-xl font-semibold text-gray-800">{stats.activeUsers}</p>
            </div>
            <Users className="w-5 h-5 text-blue-600" />
          </div>
        </div>
      </div>
    </div>
  );

  const renderNotifications = () => (
    <div className="space-y-4">
      {notifications.length === 0 ? (
        <div className="text-center py-6 text-gray-500">
          No notifications
        </div>
      ) : (
        notifications.map(notification => {
          const NotificationIcon = getNotificationIcon(notification.type);
          return (
            <div 
              key={notification.id} 
              className={`p-4 rounded-lg transition-colors duration-200 ${
                notification.read ? 'bg-gray-50' : 'bg-blue-50'
              }`}
              onClick={() => markNotificationAsRead(notification.id)}
            >
              <div className="flex items-start space-x-3">
                <NotificationIcon className={`w-5 h-5 ${
                  notification.read ? 'text-gray-500' : 'text-blue-600'
                }`} />
                <div className="flex-1">
                  <p className={`text-sm ${
                    notification.read ? 'text-gray-700' : 'text-gray-900'
                  }`}>
                    {notification.message}
                  </p>
                  <span className="text-xs text-gray-500">{notification.time}</span>
                </div>
              </div>
            </div>
          );
        })
      )}
    </div>
  );

  const renderSettings = () => (
    <div className="space-y-6">
      <div className="space-y-4">
        <h3 className="text-sm font-medium text-gray-900">Display</h3>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Moon className="w-4 h-4 text-gray-500" />
            <span className="text-sm text-gray-700">Dark Mode</span>
          </div>
          <Switch 
            checked={settings.darkMode}
            onCheckedChange={(checked) => handleSettingChange('darkMode', checked)}
          />
        </div>
      </div>

      <div className="space-y-4">
        <h3 className="text-sm font-medium text-gray-900">Notifications</h3>
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Mail className="w-4 h-4 text-gray-500" />
              <span className="text-sm text-gray-700">Email Notifications</span>
            </div>
            <Switch 
              checked={settings.emailNotifications}
              onCheckedChange={(checked) => handleSettingChange('emailNotifications', checked)}
            />
          </div>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <BellRing className="w-4 h-4 text-gray-500" />
              <span className="text-sm text-gray-700">Push Notifications</span>
            </div>
            <Switch 
              checked={settings.pushNotifications}
              onCheckedChange={(checked) => handleSettingChange('pushNotifications', checked)}
            />
          </div>
        </div>
      </div>

      <div className="space-y-4">
        <h3 className="text-sm font-medium text-gray-900">Security</h3>
        <div className="space-y-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Lock className="w-4 h-4 text-gray-500" />
              <span className="text-sm text-gray-700">Two-Factor Authentication</span>
            </div>
            <Switch 
              checked={settings.twoFactorAuth}
              onCheckedChange={(checked) => handleSettingChange('twoFactorAuth', checked)}
            />
          </div>
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Toggle className="w-4 h-4 text-gray-500" />
              <span className="text-sm text-gray-700">Auto Logout</span>
            </div>
            <select 
              value={settings.autoLogout}
              onChange={(e) => handleSettingChange('autoLogout', parseInt(e.target.value))}
              className="text-sm border rounded-md px-2 py-1"
            >
              <option value={15}>15 minutes</option>
              <option value={30}>30 minutes</option>
              <option value={60}>1 hour</option>
            </select>
          </div>
        </div>
      </div>

      <div className="space-y-4">
        <h3 className="text-sm font-medium text-gray-900">Preferences</h3>
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Globe className="w-4 h-4 text-gray-500" />
            <span className="text-sm text-gray-700">Language</span>
          </div>
          <select 
            value={settings.language}
            onChange={(e) => handleSettingChange('language', e.target.value)}
            className="text-sm border rounded-md px-2 py-1"
          >
            <option value="english">English</option>
            <option value="spanish">Spanish</option>
            <option value="french">French</option>
          </select>
        </div>
      </div>
    </div>
  );

  return (
    <>
      {/* Overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40"
          onClick={onClose}
        />
      )}

      {/* Sidebar */}
      <div className={`fixed right-0 top-0 h-full w-80 bg-white shadow-lg transform transition-transform duration-300 ease-in-out z-50 ${
        isOpen ? 'translate-x-0' : 'translate-x-full'
      }`}>
        {/* Header */}
        <div className="p-4 border-b">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 rounded-full bg-blue-100 flex items-center justify-center">
                <User className="w-5 h-5 text-blue-600" />
              </div>
              <div>
                <h2 className="font-semibold text-gray-800">Admin Panel</h2>
                <p className="text-sm text-gray-500">Form Manager</p>
              </div>
            </div>
            <button onClick={onClose} className="p-2 hover:bg-gray-100 rounded-full">
              <X className="w-5 h-5 text-gray-600" />
            </button>
          </div>
        </div>

        {/* Navigation */}
        <div className="p-4 border-b">
          <div className="flex space-x-2">
            <button
              onClick={() => setActiveSection('dashboard')}
              className={`flex-1 py-2 px-3 rounded-md text-sm font-medium ${
                activeSection === 'dashboard'
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              Dashboard
            </button>
            <button
              onClick={() => setActiveSection('notifications')}
              className={`flex-1 py-2 px-3 rounded-md text-sm font-medium relative ${
                activeSection === 'notifications'
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              Notifications
              {notifications.filter(n => !n.read).length > 0 && (
                <span className="absolute top-1 right-1 w-2 h-2 bg-blue-600 rounded-full" />
              )}
            </button>
            <button
              onClick={() => setActiveSection('settings')}
              className={`flex-1 py-2 px-3 rounded-md text-sm font-medium ${
                activeSection === 'settings'
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-600 hover:bg-gray-100'
              }`}
            >
              Settings
            </button>
          </div>
        </div>

        {/* Content */}
        <div className="p-4 space-y-6 overflow-y-auto h-[calc(100%-176px)]">
          {activeSection === 'dashboard' && renderDashboard()}
          {activeSection === 'notifications' && renderNotifications()}
          {activeSection === 'settings' && renderSettings()}
        </div>

        {/* Sign Out Button */}
        <div className="p-4 border-t">
          <button 
            onClick={handleSignOut}
            className="w-full flex items-center justify-center space-x-2 bg-red-500 hover:bg-red-600 text-white p-3 rounded-lg transition-colors duration-200"
          >
            <LogOut className="w-4 h-4" />
            <span>Sign Out</span>
          </button>
        </div>
      </div>
    </>
  );
};

export default AdminSidebar;
