import React, { useState } from 'react';
import DatePicker from 'react-multi-date-picker';
import { Calendar as CalendarIcon, Save, X } from 'lucide-react';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);
  const [isOpen, setIsOpen] = useState(false);

  const handleDateChange = (dates) => {
    setSelectedDates(dates);
  };

  const handleSave = () => {
    console.log('Saving dates:', selectedDates.map(date => date.format("YYYY-MM-DD")));
    alert('Dates saved successfully!');
    setIsOpen(false);
  };

  const customStyles = {
    calendar: {
      width: '100%',
      padding: '20px',
      background: 'white',
      border: 'none',
      borderRadius: '16px',
    },
    day: {
      width: '40px',
      height: '40px',
      margin: '2px',
      color: '#475569',
      background: '#f8fafc',
      borderRadius: '8px',
      fontSize: '0.95rem',
      transition: 'all 0.2s ease',
    },
    today: {
      background: '#f8fafc',
      color: '#3b82f6',
      fontWeight: '600',
      border: '2px solid #93c5fd',
    },
    selected: {
      background: '#fee2e2',
      color: '#991b1b',
      fontWeight: '600',
    },
    weekDays: {
      fontSize: '0.875rem',
      color: '#64748b',
      fontWeight: '600',
      textTransform: 'uppercase',
      letterSpacing: '0.05em',
    },
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-4">
      <div className="max-w-7xl mx-auto bg-white rounded-2xl shadow-lg p-6">
        <div className="flex items-center gap-3 mb-6">
          <CalendarIcon className="w-6 h-6 text-blue-600" />
          <h1 className="text-2xl font-bold text-slate-800">Multi Date Picker</h1>
        </div>

        {/* Trigger Button */}
        <button
          onClick={() => setIsOpen(true)}
          className="w-full flex items-center gap-2 px-4 py-2 text-left border rounded-lg hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
        >
          <CalendarIcon className="w-4 h-4 text-gray-500" />
          <span className="text-gray-700">
            {selectedDates.length > 0
              ? `${selectedDates.length} date${selectedDates.length > 1 ? 's' : ''} selected`
              : 'Select dates...'}
          </span>
        </button>

        {/* Custom Dialog */}
        {isOpen && (
          <div className="fixed inset-0 z-50 overflow-y-auto">
            {/* Backdrop */}
            <div 
              className="fixed inset-0 bg-black bg-opacity-25 transition-opacity"
              onClick={() => setIsOpen(false)}
            />

            {/* Dialog Panel */}
            <div className="flex min-h-full items-center justify-center p-4">
              <div className="relative bg-white rounded-xl shadow-xl max-w-lg w-full p-6">
                {/* Close Button */}
                <button
                  onClick={() => setIsOpen(false)}
                  className="absolute right-4 top-4 text-gray-400 hover:text-gray-500"
                >
                  <X className="w-5 h-5" />
                </button>

                {/* Dialog Header */}
                <div className="mb-4">
                  <h2 className="text-lg font-semibold text-gray-900">Select Dates</h2>
                </div>

                {/* Calendar */}
                <div className="w-full mb-6">
                  <DatePicker
                    value={selectedDates}
                    onChange={handleDateChange}
                    multiple
                    isClearable
                    inline
                    minDate={new Date()}
                    style={customStyles.calendar}
                    classNames={{
                      day: 'custom-day',
                      selected: 'custom-selected',
                    }}
                  />
                </div>

                {/* Selected Dates Display */}
                {selectedDates.length > 0 && (
                  <div className="mt-4 p-4 bg-blue-50 rounded-xl border border-blue-100">
                    <h3 className="text-sm font-semibold text-blue-900 mb-2">Selected Dates:</h3>
                    <div className="flex flex-wrap gap-2">
                      {selectedDates.map((date, index) => (
                        <span
                          key={index}
                          className="inline-flex items-center gap-1.5 px-3 py-1.5 bg-white rounded-lg text-sm text-blue-700 shadow-sm"
                          style={{
                            animation: `fadeIn 0.3s ease-out ${index * 0.1}s both`
                          }}
                        >
                          <CalendarIcon className="w-4 h-4" />
                          {date.format("DD/MM/YYYY")}
                        </span>
                      ))}
                    </div>
                  </div>
                )}

                {/* Actions */}
                <div className="mt-6 flex justify-end gap-3">
                  <button
                    onClick={() => setIsOpen(false)}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-lg hover:bg-gray-200"
                  >
                    Cancel
                  </button>
                  <button 
                    onClick={handleSave}
                    disabled={selectedDates.length === 0}
                    className={`
                      flex items-center gap-2 px-4 py-2 rounded-lg font-medium text-sm
                      transition-all duration-200 ease-in-out
                      ${selectedDates.length === 0 
                        ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
                        : 'bg-blue-600 text-white hover:bg-blue-700 active:transform active:scale-95'
                      }
                    `}
                  >
                    <Save className="w-4 h-4" />
                    Save Dates
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        <style jsx global>{`
          @keyframes fadeIn {
            from {
              opacity: 0;
              transform: translateY(5px);
            }
            to {
              opacity: 1;
              transform: translateY(0);
            }
          }

          .custom-day {
            ${Object.entries(customStyles.day).map(([key, value]) => `${key}: ${value};`).join('\n')}
          }
          .custom-day:hover {
            background: #f1f5f9;
            transform: scale(0.95);
          }
          .custom-selected {
            ${Object.entries(customStyles.selected).map(([key, value]) => `${key}: ${value};`).join('\n')}
          }
          .custom-selected:hover {
            background: #fecaca !important;
          }
          .rmdp-week-day {
            ${Object.entries(customStyles.weekDays).map(([key, value]) => `${key}: ${value};`).join('\n')}
          }
          .rmdp-day:not(.rmdp-disabled):not(.rmdp-day-hidden) {
            transition: all 0.2s ease;
          }
          .rmdp-today {
            ${Object.entries(customStyles.today).map(([key, value]) => `${key}: ${value};`).join('\n')}
          }
          .rmdp-wrapper {
            width: 100% !important;
          }
          .rmdp-calendar {
            width: 100% !important;
            padding: 0 !important;
          }
          .rmdp-day-picker {
            display: flex !important;
            justify-content: center !important;
            width: 100% !important;
          }
          .rmdp-month-picker, .rmdp-year-picker {
            background-color: white !important;
          }
        `}</style>
      </div>
    </div>
  );
};

export default AdminCalendar;
