import React, { useState } from 'react';
import { Calendar as CalendarIcon } from 'lucide-react';
import DatePicker from 'react-multi-date-picker';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);
  const [message, setMessage] = useState('');

  const handleDateChange = (dates) => {
    setSelectedDates(dates);
    setMessage('');
  };

  const handleSaveDates = () => {
    setMessage('Dates saved successfully!');
  };

  const customStyles = {
    calendar: {
      width: '100%',
      height: 'max-content',
      padding: '24px',
      background: 'white',
      border: 'none',
      borderRadius: '0 0 16px 16px',
      fontFamily: 'Inter, system-ui, sans-serif',
      boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1)',
    },
    header: {
      padding: '16px',
      margin: '-24px -24px 24px -24px',
      background: 'white',
      borderBottom: '1px solid #f1f5f9',
    },
    heading: {
      fontSize: '1.5rem',
      fontWeight: '600',
      color: '#1e293b',
    },
    day: {
      width: '48px',
      height: '48px',
      margin: '2px',
      color: '#475569',
      background: '#f8fafc',
      borderRadius: '12px',
      fontSize: '0.95rem',
      fontWeight: '500',
      transition: 'all 0.2s ease',
    },
    today: {
      background: '#f8fafc',
      color: '#3b82f6',
      fontWeight: '600',
      border: '2px solid #93c5fd',
    },
    selected: {
      background: '#fee2e2',
      color: '#991b1b',
      fontWeight: '600',
      transform: 'scale(0.95)',
    },
    weekDays: {
      fontSize: '0.875rem',
      color: '#64748b',
      fontWeight: '600',
      textTransform: 'uppercase',
      letterSpacing: '0.05em',
    },
  };

  return (
    <div className="w-full max-w-4xl mx-auto">
      <div className="bg-blue-600 text-white p-6 rounded-t-xl flex items-center gap-3 shadow-lg transition-all duration-300 hover:bg-blue-700">
        <CalendarIcon className="w-6 h-6 animate-pulse" />
        <span className="text-lg font-medium tracking-wide">Calendar</span>
      </div>

      <DatePicker
        value={selectedDates}
        onChange={handleDateChange}
        multiple
        format="MMMM YYYY"
        style={customStyles.calendar}
        calendarPosition="bottom-center"
        containerClassName="w-full"
        headerFormat="MMMM YYYY"
        minDate={new Date()}
        classNames={{
          header: 'custom-header',
          day: 'custom-day',
          selected: 'custom-selected',
        }}
        renderButton={(open) => (
          <div className="bg-white p-6 flex justify-between items-center border-b border-slate-100">
            <button className="px-5 py-2.5 bg-slate-800 text-white rounded-lg transition-all duration-200 hover:bg-slate-700 active:transform active:scale-95 shadow-md hover:shadow-lg">
              Previous
            </button>
            <h2 className="text-2xl font-semibold text-slate-800">November 2024</h2>
            <div className="w-20"></div>
          </div>
        )}
      />

      {selectedDates.length > 0 && (
        <div className="mt-6 p-6 bg-blue-50 rounded-xl border border-blue-100 shadow-sm transition-all duration-300 hover:shadow-md">
          <h3 className="text-blue-900 font-semibold mb-3 text-lg">Selected Dates:</h3>
          <ul className="space-y-2">
            {selectedDates.map((date, index) => (
              <li 
                key={index} 
                className="flex items-center gap-3 text-blue-700 p-3 bg-blue-100/50 rounded-lg transition-all duration-200 hover:bg-blue-100"
                style={{
                  animation: `fadeIn 0.5s ease-out ${index * 0.1}s both`
                }}
              >
                <CalendarIcon className="w-4 h-4" />
                {date.format("dddd, MMMM D, YYYY")}
              </li>
            ))}
          </ul>
          <button
            onClick={handleSaveDates}
            className="mt-5 px-6 py-2.5 bg-blue-600 text-white rounded-lg transition-all duration-200 hover:bg-blue-700 active:transform active:scale-95 shadow-md hover:shadow-lg font-medium"
          >
            Save Dates
          </button>
          {message && (
            <p className="mt-3 text-green-600 bg-green-50 p-3 rounded-lg border border-green-100 animate-fadeIn">
              {message}
            </p>
          )}
        </div>
      )}

      <style jsx global>{`
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .animate-fadeIn {
          animation: fadeIn 0.3s ease-out forwards;
        }

        .custom-header {
          ${Object.entries(customStyles.header).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .custom-day {
          ${Object.entries(customStyles.day).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .custom-day:hover {
          background: #f1f5f9;
          transform: scale(0.98);
        }
        .custom-selected {
          ${Object.entries(customStyles.selected).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .custom-selected:hover {
          background: #fecaca !important;
        }
        .rmdp-week-day {
          ${Object.entries(customStyles.weekDays).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .rmdp-header-values {
          ${Object.entries(customStyles.heading).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .rmdp-wrapper {
          border: none !important;
          box-shadow: none !important;
        }
        .rmdp-calendar {
          padding: 0 !important;
        }
        .rmdp-day:not(.rmdp-disabled):not(.rmdp-day-hidden) {
          transition: all 0.2s ease;
        }
        .rmdp-today {
          ${Object.entries(customStyles.today).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
      `}</style>
    </div>
  );
};

export default AdminCalendar;
