import React, { useState } from 'react';
import Datepicker from 'react-multi-date-picker';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);
  const [message, setMessage] = useState('');

  const handleDateChange = (dates) => {
    setSelectedDates(dates);
    setMessage(''); // Reset message when selecting new dates
  };

  const handleSaveDates = () => {
    // Implement save functionality here if needed
    setMessage('Dates saved successfully!');
  };

  return (
    <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
      <h1 className="text-xl font-bold mb-4 text-gray-800">Select Multiple Dates</h1>
      
      <Datepicker
        value={selectedDates} // Use 'value' instead of 'selected'
        onChange={handleDateChange}
        multiple
        isClearable
        inline
        className="bg-white rounded-md border border-gray-300 p-2" // Custom styling for the date picker
      />

      {selectedDates.length > 0 && (
        <div className="mt-6 p-4 bg-blue-50 rounded-md border border-blue-200">
          <h3 className="text-lg font-semibold text-blue-800 mb-2">Selected Dates:</h3>
          <ul className="space-y-1">
            {selectedDates.map((date, index) => (
              <li key={index} className="flex items-center text-blue-700">
                <span className="mr-2">ðŸ“…</span>
                {date.format("DD/MM/YYYY")}
              </li>
            ))}
          </ul>
          <button
            onClick={handleSaveDates}
            className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Save Dates
          </button>
          {message && (
            <p className={`mt-2 ${message.includes('Error') ? 'text-red-600' : 'text-green-600'}`}>
              {message}
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default AdminCalendar;
