import React, { useState } from 'react';
import Datepicker from 'react-multi-date-picker';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);

  const handleDateChange = (dates) => {
    setSelectedDates(dates);
  };

  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-50">
      <div className="bg-white p-8 rounded-lg shadow-lg">
        <h1 className="text-3xl font-bold text-indigo-600 mb-6">Multi Date Picker</h1>
        <Datepicker
          value={selectedDates} // Use 'value' instead of 'selected'
          onChange={handleDateChange}
          multiple // 'multiple' prop instead of 'mode="multiple"'
          isClearable
          inline
          className="mb-4 border rounded-lg"
        />
        <p className="text-lg font-medium text-gray-700">
          Selected Dates:{" "}
          <span className="text-indigo-500">
            {selectedDates.map(date => date.format("DD/MM/YYYY")).join(", ")}
          </span>
        </p>
      </div>
    </div>
  );
};

export default AdminCalendar;
