import React, { useState } from 'react';
import { Calendar } from 'lucide-react';

const AdminCalendar = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const [currentMonth, setCurrentMonth] = useState(new Date(2024, 10)); // November 2024

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const firstDayOfMonth = new Date(year, month, 1).getDay();
    
    const days = [];
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(i);
    }
    return { days, firstDayOfMonth };
  };

  const { days } = getDaysInMonth(currentMonth);

  const handleDateClick = (day) => {
    const newDate = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), day);
    setSelectedDate(newDate);
  };

  const formatDate = (date) => {
    if (!date) return '';
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];
    
    return `${days[date.getDay()]}, ${months[date.getMonth()]} ${date.getDate()}, ${date.getFullYear()}`;
  };

  return (
    <div className="w-full max-w-4xl mx-auto">
      <div className="bg-blue-600 text-white p-4 rounded-t-lg flex items-center gap-2">
        <Calendar className="w-6 h-6" />
        <span className="text-lg">Calendar</span>
      </div>
      
      <div className="bg-white p-6 rounded-b-lg shadow-lg">
        <div className="flex justify-between items-center mb-6">
          <button className="px-4 py-2 bg-gray-800 text-white rounded">Previous</button>
          <h2 className="text-2xl font-semibold">November 2024</h2>
          <div className="w-20"></div>
        </div>

        <div className="grid grid-cols-7 gap-1">
          {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map((day) => (
            <div key={day} className="p-4 text-center font-medium">
              {day}
            </div>
          ))}
          
          {days.map((day) => (
            <button
              key={day}
              onClick={() => handleDateClick(day)}
              className={`p-4 text-center hover:bg-gray-100 ${
                selectedDate?.getDate() === day ? 'bg-red-200' : 'bg-gray-100'
              }`}
            >
              {day}
            </button>
          ))}
        </div>

        {selectedDate && (
          <div className="mt-6 p-4 bg-blue-50 rounded">
            <h3 className="text-blue-800 font-semibold mb-2">Selected Dates:</h3>
            <div className="flex items-center gap-2 text-blue-700">
              <Calendar className="w-4 h-4" />
              {formatDate(selectedDate)}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminCalendar;
