// Client-side React component (AdminCalendar.jsx)
import React, { useState, useEffect, useCallback } from 'react';
import Calendar from './Calendar'; // Your existing Calendar component
import { CalendarIcon } from 'lucide-react';
import axios from 'axios';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);
  const [isSaving, setIsSaving] = useState(false);
  const [message, setMessage] = useState('');

  // Fetch existing dates when component mounts
  useEffect(() => {
    fetchBusyDates();
  }, []);

  const fetchBusyDates = async () => {
    try {
      const response = await axios.get('http://localhost:3000/api/admin/busy-dates');
      setSelectedDates(response.data.dates.map(date => new Date(date.date)));
    } catch (error) {
      setMessage('Error fetching dates');
      console.error('Error:', error);
    }
  };

  const handleSelectDates = (dates) => {
    setSelectedDates(dates);
  };

  const handleSaveDates = async () => {
    setIsSaving(true);
    setMessage('');

    try {
      // Format dates properly for the API
      const formattedDates = selectedDates.map(date => {
        // Ensure we're working with a Date object
        const dateObj = new Date(date);
        // Format as YYYY-MM-DD
        return dateObj.toISOString().split('T')[0];
      });

      await axios.post('http://localhost:3000/api/admin/busy-dates', {
        dates: formattedDates
      });
      setMessage('Dates saved successfully!');
    } catch (error) {
      setMessage('Error saving dates');
      console.error('Error:', error);
    } finally {
      setIsSaving(false);
    }
  };

  const formatDisplayDate = useCallback((dateString) => {
    const [year, month, day] = dateString.split('-');
    const date = new Date(year, month - 1, parseInt(day) + 1);
    return date.toLocaleDateString();
  }, []);


  return (
    <div className="bg-white shadow-lg rounded-lg p-6 mb-6">
      <div>
        <Calendar onSelectDates={handleSelectDates} selectedDates={selectedDates} />
        {selectedDates.length > 0 && (
          <div className="mt-6 p-4 bg-blue-50 rounded-md border border-blue-200">
            <h3 className="text-lg font-semibold text-blue-800 mb-2">Selected Dates:</h3>
            <ul className="space-y-1">
              {selectedDates.map((date) => (
                <li key={date.toString()} className="flex items-center text-blue-700">
                  <CalendarIcon className="mr-2" size={16} />
                  {formatDisplayDate(date)}
                </li>
              ))}
            </ul>
            <button
              onClick={handleSaveDates}
              disabled={isSaving}
              className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-blue-300"
            >
              {isSaving ? 'Saving...' : 'Save Dates'}
            </button>
            {message && (
              <p className={`mt-2 ${message.includes('Error') ? 'text-red-600' : 'text-green-600'}`}>
                {message}
              </p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminCalendar;
