import React, { useState, useEffect } from 'react';
import { format, startOfWeek, addDays, isSameDay } from 'date-fns';

const Calendar = ({ onSelectDates, selectedDates }) => {
  const [currentDate, setCurrentDate] = useState(new Date());

  useEffect(() => {
    onSelectDates(selectedDates);
  }, [selectedDates, onSelectDates]);

  const renderCalendar = () => {
    const year = currentDate.getFullYear();
    const month = currentDate.getMonth();
    const firstDay = startOfWeek(new Date(year, month, 1));
    const lastDay = new Date(year, month + 1, 0);
    const days = [];

    let currentDay = firstDay;
    while (currentDay <= lastDay) {
      days.push(
        <div
          key={currentDay.toString()}
          className={`day w-full h-12 flex items-center justify-center bg-gray-300 cursor-pointer transition duration-300 ease-in-out hover:bg-red-400 ${
            selectedDates.some(date => isSameDay(currentDay, date)) ? 'bg-red-300' : ''
          }`}
          onClick={() => toggleDate(currentDay)}
        >
          {format(currentDay, 'd')}
        </div>
      );
      currentDay = addDays(currentDay, 1);
    }

    return days;
  };

  const toggleDate = (date) => {
    if (!isValid(date)) {
      return;
    }

    const updatedDates = selectedDates.some(d => isSameDay(d, date))
      ? selectedDates.filter(d => !isSameDay(d, date))
      : [...selectedDates, date];

    onSelectDates(updatedDates);
  };

  const isValid = (date) => {
    return !isNaN(date.getTime());
  };

  return (
    <div>
      <div className="calendar-nav flex justify-between items-center mb-2">
        <button
          className="py-1 px-2 bg-gray-800 text-white rounded hover:bg-blue-800"
          onClick={() => setCurrentDate(addDays(currentDate, -32))}
        >
          Previous
        </button>
        <h2 className="text-lg">{format(currentDate, 'MMMM yyyy')}</h2>
        <button
          className="py-1 px-2 bg-gray-800 text-white rounded hover:bg-blue-800"
          onClick={() => setCurrentDate(addDays(currentDate, 32))}
        >
          Next
        </button>
      </div>
      <div className="calendar grid grid-cols-7 gap-1 mt-5">{renderCalendar()}</div>
    </div>
  );
};

export default Calendar;
