import React, { useState, useEffect } from 'react';
import { User, Search as SearchIcon, Users, FileText, Mail, Clock, BarChart2 } from 'lucide-react';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, Cell, ResponsiveContainer, XAxis, YAxis, Tooltip } from 'recharts';
import FormCard from './Formcard';
import Sidebar from './AdminSidebar';

const IPRLogo = () => (
  <svg viewBox="0 0 100 100" className="w-8 h-8">
    <circle cx="50" cy="50" r="45" fill="#1a4789" />
    <circle cx="50" cy="50" r="35" fill="none" stroke="white" strokeWidth="2" />
    <circle cx="50" cy="50" r="25" fill="none" stroke="white" strokeWidth="2" />
    <ellipse cx="50" cy="50" rx="42" ry="15" fill="none" stroke="white" strokeWidth="2" transform="rotate(45 50 50)" />
    <ellipse cx="50" cy="50" rx="42" ry="15" fill="none" stroke="white" strokeWidth="2" transform="rotate(-45 50 50)" />
    <circle cx="50" cy="50" r="5" fill="white" />
  </svg>
);

const DashboardMetrics = ({ stats }) => {
  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-4 mb-6">
      {/* Summary Cards */}
      <div className="grid grid-cols-2 gap-4">
        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-500">Active Forms</p>
              <p className="text-xl font-semibold">{stats.activeForms}</p>
              <p className="text-xs text-green-600 mt-1">
                +{stats.newFormsToday} today
              </p>
            </div>
            <FileText className="w-8 h-8 text-blue-600" />
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-500">Total Users</p>
              <p className="text-xl font-semibold">{stats.totalUsers}</p>
              <p className="text-xs text-green-600 mt-1">
                +{stats.newUsers} this week
              </p>
            </div>
            <Users className="w-8 h-8 text-purple-600" />
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-500">Submissions</p>
              <p className="text-xl font-semibold">{stats.totalSubmissions}</p>
              <p className="text-xs text-green-600 mt-1">
                {stats.submissionRate}% rate
              </p>
            </div>
            <Mail className="w-8 h-8 text-green-600" />
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow-sm border">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-500">Avg. Response</p>
              <p className="text-xl font-semibold">{stats.avgResponseTime}h</p>
              <p className="text-xs text-blue-600 mt-1">
                Processing time
              </p>
            </div>
            <Clock className="w-8 h-8 text-orange-600" />
          </div>
        </div>
      </div>

      {/* Submission Trends Chart */}
      <div className="bg-white p-4 rounded-lg shadow-sm border">
        <h3 className="text-sm font-medium text-gray-700 mb-4">Submission Trends</h3>
        <div className="h-48">
          <ResponsiveContainer width="100%" height="100%">
            <LineChart data={stats.submissionTrends}>
              <XAxis dataKey="date" tickFormatter={(date) => new Date(date).toLocaleDateString()} />
              <YAxis />
              <Tooltip labelFormatter={(date) => new Date(date).toLocaleDateString()} />
              <Line type="monotone" dataKey="submissions" stroke="#2563eb" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Form Categories Distribution */}
      <div className="bg-white p-4 rounded-lg shadow-sm border">
        <h3 className="text-sm font-medium text-gray-700 mb-4">Form Categories</h3>
        <div className="h-48">
          <ResponsiveContainer width="100%" height="100%">
            <PieChart>
              <Pie
                data={stats.formCategories}
                dataKey="value"
                nameKey="name"
                cx="50%"
                cy="50%"
                outerRadius={80}
                label
              >
                {stats.formCategories.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>
      </div>

      {/* Response Time Distribution */}
      <div className="bg-white p-4 rounded-lg shadow-sm border">
        <h3 className="text-sm font-medium text-gray-700 mb-4">Response Time Distribution</h3>
        <div className="h-48">
          <ResponsiveContainer width="100%" height="100%">
            <BarChart data={stats.responseTimeDistribution}>
              <XAxis dataKey="range" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="count" fill="#2563eb" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

const Dashboard = ({ forms, organizationName = "Institute of Plasma Research" }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isAdminSidebarOpen, setIsAdminSidebarOpen] = useState(false);
  const [searchFocused, setSearchFocused] = useState(false);
  const [dashboardStats, setDashboardStats] = useState({
    activeForms: 45,
    newFormsToday: 3,
    totalUsers: 1250,
    newUsers: 28,
    totalSubmissions: 3420,
    submissionRate: 87,
    avgResponseTime: 4.5,
    submissionTrends: [
      { date: '2024-01-01', submissions: 45 },
      { date: '2024-01-02', submissions: 52 },
      { date: '2024-01-03', submissions: 48 },
      { date: '2024-01-04', submissions: 70 },
      { date: '2024-01-05', submissions: 61 },
      { date: '2024-01-06', submissions: 65 },
      { date: '2024-01-07', submissions: 75 }
    ],
    formCategories: [
      { name: 'Administrative', value: 35 },
      { name: 'Research', value: 45 },
      { name: 'HR', value: 15 },
      { name: 'Finance', value: 25 }
    ],
    responseTimeDistribution: [
      { range: '0-2h', count: 145 },
      { range: '2-4h', count: 232 },
      { range: '4-8h', count: 117 },
      { range: '8-24h', count: 85 },
      { range: '24h+', count: 35 }
    ]
  });

  useEffect(() => {
    setFilteredForms(forms);
    setIsLoading(false);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white">
      <header className="bg-white border-b border-gray-100">
        <div className="px-3 lg:px-5">
          <div className="flex items-center justify-between h-14">
            <div className="flex items-center space-x-3 group">
              <div className="transform transition-transform group-hover:scale-105">
                <IPRLogo />
              </div>
              <h1 className="text-lg font-semibold text-gray-900 hidden sm:block tracking-tight">
                {organizationName}
              </h1>
            </div>

            <div className="flex items-center space-x-3 flex-1 justify-end max-w-xl">
              <div className={`relative flex-1 max-w-md hidden sm:block transition-all duration-200 ${searchFocused ? 'scale-105' : ''}`}>
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <SearchIcon size={16} className="text-gray-400" />
                </div>
                <input
                  type="text"
                  className="w-full pl-10 pr-4 py-1.5 text-sm text-gray-900 placeholder-gray-400 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-white transition-all"
                  placeholder="Search forms..."
                  onChange={(e) => handleSearch(e.target.value)}
                  onFocus={() => setSearchFocused(true)}
                  onBlur={() => setSearchFocused(false)}
                />
              </div>
              <button
                onClick={() => setIsAdminSidebarOpen(true)}
                className="p-2 text-gray-500 hover:text-gray-700 rounded-lg hover:bg-gray-50 transition-colors duration-200 transform hover:scale-105"
              >
                <User size={20} />
              </button>
            </div>
          </div>
          
          <div className="sm:hidden py-2">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <SearchIcon size={16} className="text-gray-400" />
              </div>
              <input
                type="text"
                className="w-full pl-10 pr-4 py-1.5 text-sm text-gray-900 placeholder-gray-400 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                placeholder="Search forms..."
                onChange={(e) => handleSearch(e.target.value)}
              />
            </div>
          </div>
        </div>
      </header>

      <main className="px-3 lg:px-5 py-4">
        {/* Dashboard Metrics */}
        <DashboardMetrics stats={dashboardStats} />

        {/* Forms Grid */}
        {isLoading ? (
          <div className="flex justify-center items-center h-32">
            <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : filteredForms.length === 0 ? (
          <div className="text-center py-12 animate-fade-in">
            <div className="inline-block p-4 rounded-full bg-gray-50 mb-4">
              <SearchIcon size={24} className="text-gray-400" />
            </div>
            <p className="text-gray-500">No forms found</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 animate-fade-in">
            {filteredForms.map((form, index) => (
              <div
                key={form.id}
                className="transform transition-all duration-300 hover:scale-102 hover:-translate-y-1"
                style={{
                  animationDelay: `${index * 50}ms`
                }}
              >
                <FormCard form={form} />
              </div>
            ))}
          </div>
        )}
      </main>

      <Sidebar
        isOpen={isAdminSidebarOpen}
        onClose={() => setIsAdminSidebarOpen(false)}
      />

      <style jsx global>{`
        @keyframes fade-in {
          from {
            opacity: 0;
            transform: translateY(10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-fade-in {
          animation: fade-in 0.4s ease-out forwards;
        }
        
        .hover\\:scale-102:hover {
          transform: scale(1.02);
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
