import React, { useState } from 'react';
import DatePicker from 'react-multi-date-picker';
import { Calendar as CalendarIcon } from 'lucide-react';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);

  const handleDateChange = (dates) => {
    setSelectedDates(dates);
  };

  const customStyles = {
    calendar: {
      width: '100%',
      maxWidth: '400px',
      padding: '20px',
      background: 'white',
      border: 'none',
      borderRadius: '16px',
      boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1), 0 2px 4px -2px rgb(0 0 0 / 0.1)',
    },
    day: {
      width: '40px',
      height: '40px',
      margin: '2px',
      color: '#475569',
      background: '#f8fafc',
      borderRadius: '8px',
      fontSize: '0.95rem',
      transition: 'all 0.2s ease',
    },
    today: {
      background: '#f8fafc',
      color: '#3b82f6',
      fontWeight: '600',
      border: '2px solid #93c5fd',
    },
    selected: {
      background: '#fee2e2',
      color: '#991b1b',
      fontWeight: '600',
    },
    weekDays: {
      fontSize: '0.875rem',
      color: '#64748b',
      fontWeight: '600',
      textTransform: 'uppercase',
      letterSpacing: '0.05em',
    },
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-4">
      <div className="bg-white rounded-2xl shadow-lg p-6 w-full max-w-lg">
        <div className="flex items-center gap-3 mb-6">
          <CalendarIcon className="w-6 h-6 text-blue-600" />
          <h1 className="text-2xl font-bold text-slate-800">Multi Date Picker</h1>
        </div>

        <div className="flex justify-center mb-6">
          <DatePicker
            value={selectedDates}
            onChange={handleDateChange}
            multiple
            isClearable
            inline
            style={customStyles.calendar}
            classNames={{
              day: 'custom-day',
              selected: 'custom-selected',
            }}
          />
        </div>

        {selectedDates.length > 0 && (
          <div className="mt-4 p-4 bg-blue-50 rounded-xl border border-blue-100">
            <h3 className="text-sm font-semibold text-blue-900 mb-2">Selected Dates:</h3>
            <div className="flex flex-wrap gap-2">
              {selectedDates.map((date, index) => (
                <span
                  key={index}
                  className="inline-flex items-center gap-1.5 px-3 py-1.5 bg-white rounded-lg text-sm text-blue-700 shadow-sm"
                  style={{
                    animation: `fadeIn 0.3s ease-out ${index * 0.1}s both`
                  }}
                >
                  <CalendarIcon className="w-4 h-4" />
                  {date.format("DD/MM/YYYY")}
                </span>
              ))}
            </div>
          </div>
        )}
      </div>

      <style jsx global>{`
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(5px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .custom-day {
          ${Object.entries(customStyles.day).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .custom-day:hover {
          background: #f1f5f9;
          transform: scale(0.95);
        }
        .custom-selected {
          ${Object.entries(customStyles.selected).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .custom-selected:hover {
          background: #fecaca !important;
        }
        .rmdp-week-day {
          ${Object.entries(customStyles.weekDays).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .rmdp-day:not(.rmdp-disabled):not(.rmdp-day-hidden) {
          transition: all 0.2s ease;
        }
        .rmdp-today {
          ${Object.entries(customStyles.today).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .rmdp-wrapper {
          border: none !important;
          box-shadow: none !important;
        }
      `}</style>
    </div>
  );
};

export default AdminCalendar;
