import React, { useState } from 'react';
import DatePicker from 'react-multi-date-picker';
import { Calendar as CalendarIcon } from 'lucide-react';

const AdminCalendar = () => {
  const [selectedDates, setSelectedDates] = useState([]);

  const handleDateChange = (dates) => {
    setSelectedDates(dates);
  };

  const customStyles = {
    calendar: {
      width: '100%',
      maxWidth: '100%', // Changed to allow full width
      padding: '24px',
      background: 'white',
      border: 'none',
      borderRadius: '16px',
      boxShadow: 'none',
    },
    day: {
      width: '48px', // Increased size
      height: '48px', // Increased size
      margin: '4px',
      color: '#475569',
      background: '#f8fafc',
      borderRadius: '12px',
      fontSize: '1rem',
      transition: 'all 0.3s ease',
    },
    today: {
      background: '#f8fafc',
      color: '#3b82f6',
      fontWeight: '600',
      border: '2px solid #93c5fd',
    },
    selected: {
      background: '#fee2e2',
      color: '#991b1b',
      fontWeight: '600',
    },
    weekDays: {
      fontSize: '0.875rem',
      color: '#64748b',
      fontWeight: '600',
      textTransform: 'uppercase',
      letterSpacing: '0.05em',
      padding: '16px 0', // Added more padding
    },
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-6">
      <div className="max-w-7xl mx-auto"> {/* Increased max width */}
        <div className="bg-white rounded-2xl shadow-xl p-8">
          <div className="flex items-center justify-between mb-8">
            <div className="flex items-center gap-3">
              <CalendarIcon className="w-8 h-8 text-blue-600" />
              <h1 className="text-3xl font-bold text-gray-800">Multi Date Picker</h1>
            </div>
          </div>

          <div className="w-full mb-8"> {/* Removed justify-center to allow full width */}
            <DatePicker
              value={selectedDates}
              onChange={handleDateChange}
              multiple
              isClearable
              inline
              style={customStyles.calendar}
              classNames={{
                day: 'custom-day',
                selected: 'custom-selected',
              }}
            />
          </div>

          {selectedDates.length > 0 && (
            <div className="mt-8 p-6 bg-blue-50 rounded-xl border border-blue-100">
              <h3 className="text-lg font-semibold text-blue-900 mb-4">Selected Dates</h3>
              <div className="flex flex-wrap gap-3">
                {selectedDates.map((date, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center gap-2 px-4 py-2 bg-white rounded-lg text-sm text-blue-700 shadow-sm"
                    style={{
                      animation: `fadeIn 0.3s ease-out ${index * 0.1}s both`
                    }}
                  >
                    <CalendarIcon className="w-4 h-4" />
                    {date.format("DD/MM/YYYY")}
                  </span>
                ))}
              </div>
            </div>
          )}
        </div>
      </div>

      <style jsx global>{`
        @keyframes fadeIn {
          from {
            opacity: 0;
            transform: translateY(5px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .custom-day {
          ${Object.entries(customStyles.day).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .custom-day:hover {
          background: #f1f5f9;
          transform: scale(0.95);
        }
        .custom-selected {
          ${Object.entries(customStyles.selected).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .custom-selected:hover {
          background: #fecaca !important;
        }
        .rmdp-week-day {
          ${Object.entries(customStyles.weekDays).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .rmdp-day:not(.rmdp-disabled):not(.rmdp-day-hidden) {
          transition: all 0.3s ease;
        }
        .rmdp-today {
          ${Object.entries(customStyles.today).map(([key, value]) => `${key}: ${value};`).join('\n')}
        }
        .rmdp-wrapper {
          border: none !important;
          box-shadow: none !important;
          width: 100% !important;
        }
        .rmdp-calendar {
          width: 100% !important;
          padding: 0 !important;
        }
      `}</style>
    </div>
  );
};

export default AdminCalendar;
