import React, { useState, useEffect } from 'react';
import { User, Search as SearchIcon } from 'lucide-react';
import FormCard from './Formcard';
import SearchBar from './SearchBar';
import Sidebar from './AdminSidebar';
import logoImage from './logo.png'; 

const Dashboard = ({ forms, organizationName = "Institue of Plasma Research" }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isAdminSidebarOpen, setIsAdminSidebarOpen] = useState(false);
  const [searchFocused, setSearchFocused] = useState(false);

  useEffect(() => {
    setFilteredForms(forms);
    setIsLoading(false);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-white">
      {/* Enhanced Header with subtle gradient and animation */}
      <header className="bg-white border-b border-gray-100">
        <div className="px-3 lg:px-5">
          <div className="flex items-center justify-between h-14">
            {/* Animated Logo and Organization Name */}
            <div className="flex items-center space-x-3 group">
            <div className="w-8 h-8 flex items-center justify-center transform transition-transform group-hover:scale-105 group-hover:rotate-3">

                
                {/* Option 2: If using import (uncomment this and comment out Option 1)*/
                <img
                  src={logoImage}
                  alt={organizationName}
                  className="w-full h-full object-contain"
                /> }
              </div>
              <h1 className="text-lg font-semibold text-gray-900 hidden sm:block tracking-tight">
                {organizationName}
              </h1>
            </div>

            {/* Search and Profile with animations */}
            <div className="flex items-center space-x-3 flex-1 justify-end max-w-xl">
              <div className={`relative flex-1 max-w-md hidden sm:block transition-all duration-200 ${searchFocused ? 'scale-105' : ''}`}>
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <SearchIcon size={16} className="text-gray-400" />
                </div>
                <input
                  type="text"
                  className="w-full pl-10 pr-4 py-1.5 text-sm text-gray-900 placeholder-gray-400 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 focus:ring-offset-white transition-all"
                  placeholder="Search forms..."
                  onChange={(e) => handleSearch(e.target.value)}
                  onFocus={() => setSearchFocused(true)}
                  onBlur={() => setSearchFocused(false)}
                />
              </div>
              <button
                onClick={() => setIsAdminSidebarOpen(true)}
                className="p-2 text-gray-500 hover:text-gray-700 rounded-lg hover:bg-gray-50 transition-colors duration-200 transform hover:scale-105"
              >
                <User size={20} />
              </button>
            </div>
          </div>
          {/* Mobile Search */}
          <div className="sm:hidden py-2">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <SearchIcon size={16} className="text-gray-400" />
              </div>
              <input
                type="text"
                className="w-full pl-10 pr-4 py-1.5 text-sm text-gray-900 placeholder-gray-400 bg-gray-50 border border-gray-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all"
                placeholder="Search forms..."
                onChange={(e) => handleSearch(e.target.value)}
              />
            </div>
          </div>
        </div>
      </header>

      {/* Main Content with Animation */}
      <main className="px-3 lg:px-5 py-4">
        {isLoading ? (
          <div className="flex justify-center items-center h-32">
            <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : filteredForms.length === 0 ? (
          <div className="text-center py-12 animate-fade-in">
            <div className="inline-block p-4 rounded-full bg-gray-50 mb-4">
              <SearchIcon size={24} className="text-gray-400" />
            </div>
            <p className="text-gray-500">No forms found</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4 animate-fade-in">
            {filteredForms.map((form, index) => (
              <div
                key={form.id}
                className="transform transition-all duration-300 hover:scale-102 hover:-translate-y-1"
                style={{
                  animationDelay: `${index * 50}ms`
                }}
              >
                <FormCard form={form} />
              </div>
            ))}
          </div>
        )}
      </main>

      <Sidebar
        isOpen={isAdminSidebarOpen}
        onClose={() => setIsAdminSidebarOpen(false)}
      />

      <style jsx global>{`
        @keyframes fade-in {
          from {
            opacity: 0;
            transform: translateY(10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-fade-in {
          animation: fade-in 0.4s ease-out forwards;
        }
        
        .hover\:scale-102:hover {
          transform: scale(1.02);
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
