import React, { useState, useEffect } from 'react';
import { User, Search as SearchIcon } from 'lucide-react';
import FormCard from './Formcard';
import SearchBar from './SearchBar';
import Sidebar from './AdminSidebar';
import logoImage from './logo.png';

const Dashboard = ({ forms, organizationName = "Institute for Plasma Research" }) => {
  const [filteredForms, setFilteredForms] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isAdminSidebarOpen, setIsAdminSidebarOpen] = useState(false);
  const [searchFocused, setSearchFocused] = useState(false);

  useEffect(() => {
    setFilteredForms(forms);
    setIsLoading(false);
  }, [forms]);

  const handleSearch = (query) => {
    if (!query) {
      setFilteredForms(forms);
      return;
    }
    const filtered = forms.filter((form) =>
      form.title.toLowerCase().includes(query.toLowerCase())
    );
    setFilteredForms(filtered);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-orange-50 to-white">
      {/* Enhanced Header with IPR styling */}
      <header className="bg-gradient-to-r from-orange-100 via-orange-50 to-blue-50 border-b border-orange-200 shadow-sm">
        <div className="px-4 lg:px-6">
          <div className="flex items-center justify-between h-20">
            {/* Logo and Organization Name with IPR styling */}
            <div className="flex items-center space-x-4 group">
              <div className="w-12 h-12 flex items-center justify-center transform transition-transform group-hover:scale-105">
                <img
                  src={logoImage}
                  alt={organizationName}
                  className="w-full h-full object-contain"
                />
              </div>
              <div className="flex flex-col">
                <h1 className="text-2xl font-bold text-orange-500 hidden sm:block tracking-wide">
                  प्लाज्मा अनुसंधान संस्थान
                </h1>
                <h2 className="text-xl font-semibold text-blue-600 hidden sm:block tracking-wide">
                  Institute for Plasma Research
                </h2>
              </div>
            </div>

            {/* Search and Profile with updated styling */}
            <div className="flex items-center space-x-4 flex-1 justify-end max-w-xl">
              <div className={`relative flex-1 max-w-md hidden sm:block transition-all duration-200 ${searchFocused ? 'scale-105' : ''}`}>
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <SearchIcon size={16} className="text-orange-400" />
                </div>
                <input
                  type="text"
                  className="w-full pl-10 pr-4 py-2 text-sm text-gray-900 placeholder-gray-400 bg-white border border-orange-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-400 focus:ring-offset-2 focus:ring-offset-orange-50 transition-all"
                  placeholder="Search forms..."
                  onChange={(e) => handleSearch(e.target.value)}
                  onFocus={() => setSearchFocused(true)}
                  onBlur={() => setSearchFocused(false)}
                />
              </div>
              <button
                onClick={() => setIsAdminSidebarOpen(true)}
                className="p-2 text-blue-600 hover:text-blue-700 rounded-lg hover:bg-blue-50 transition-colors duration-200 transform hover:scale-105"
              >
                <User size={24} />
              </button>
            </div>
          </div>
          {/* Mobile Search with updated styling */}
          <div className="sm:hidden py-3">
            <div className="relative">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <SearchIcon size={16} className="text-orange-400" />
              </div>
              <input
                type="text"
                className="w-full pl-10 pr-4 py-2 text-sm text-gray-900 placeholder-gray-400 bg-white border border-orange-200 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-400 focus:border-transparent transition-all"
                placeholder="Search forms..."
                onChange={(e) => handleSearch(e.target.value)}
              />
            </div>
          </div>
        </div>
      </header>

      {/* Main Content with IPR theme */}
      <main className="px-4 lg:px-6 py-6">
        {isLoading ? (
          <div className="flex justify-center items-center h-32">
            <div className="animate-spin rounded-full h-8 w-8 border-t-2 border-b-2 border-blue-600"></div>
          </div>
        ) : filteredForms.length === 0 ? (
          <div className="text-center py-12 animate-fade-in">
            <div className="inline-block p-4 rounded-full bg-orange-50 mb-4">
              <SearchIcon size={24} className="text-orange-400" />
            </div>
            <p className="text-gray-600">No forms found</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 animate-fade-in">
            {filteredForms.map((form, index) => (
              <div
                key={form.id}
                className="transform transition-all duration-300 hover:scale-102 hover:-translate-y-1"
                style={{
                  animationDelay: `${index * 50}ms`
                }}
              >
                <div className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow p-4 border border-orange-100">
                  <h3 className="text-lg font-semibold text-blue-600 mb-2">{form.title}</h3>
                  <p className="text-gray-600 text-sm mb-4">{form.description}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-orange-500 text-sm font-medium">
                      {form.status}
                    </span>
                    <button className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 transition-colors text-sm font-medium">
                      View Details
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </main>

      <Sidebar
        isOpen={isAdminSidebarOpen}
        onClose={() => setIsAdminSidebarOpen(false)}
      />

      <style jsx global>{`
        @keyframes fade-in {
          from {
            opacity: 0;
            transform: translateY(10px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        .animate-fade-in {
          animation: fade-in 0.4s ease-out forwards;
        }
        
        .hover\:scale-102:hover {
          transform: scale(1.02);
        }

        /* Custom scrollbar for webkit browsers */
        ::-webkit-scrollbar {
          width: 8px;
          height: 8px;
        }

        ::-webkit-scrollbar-track {
          background: #f1f1f1;
        }

        ::-webkit-scrollbar-thumb {
          background: #f97316;
          border-radius: 4px;
        }

        ::-webkit-scrollbar-thumb:hover {
          background: #ea580c;
        }
      `}</style>
    </div>
  );
};

export default Dashboard;
