import React, { useState } from "react";
import { useNavigate } from 'react-router-dom';

const IPRForm = () => {
    const [formData, setFormData] = useState({
        email: ""
      });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
        ...prevData,
        [name]: value,
        }));
    };
    const navigate = useNavigate();  // Create a navigate function

    // Define the handleSubmit function
    const handleSubmit = (event) => {
        event.preventDefault();  // Prevent default form submission behavior
        // Navigate to the second page when the form is submitted
        navigate('/IPRPage2');
    }

    return (
        <div className="bg-gray-100 p-12 flex justify-center items-center h-screen w-auto">
            <div className="bg-white p-8 border border-blue-500 rounded-lg shadow-lg w-full">
            <h1 className='text-3xl font-bold text-gray-800 text-center mb-4'>Application form - National Science Day-NSD2024</h1>
            <form onSubmit={handleSubmit}>
                <p className="text-gray-600 mb-6 mt-8 text-justify">
                Institute for Plasma Research, Bhat, Gandhinagar, Gujarat is conducting National Science Day 2024 at its Bhat, Gandhinagar campus on February 10th and 11th 2024 (Saturday and Sunday ) for school students and teachers across Gujarat. This is the application form to be filled by schools regarding their participants (students and teachers) to participate in various competitions to be conducted for the celebration of NSD2024.
                </p>
                <p className="text-gray-600 mb-4 text-justify">
                Kindly, go through the instructions given and refer the schedule of the event-competitions attached with the Invitation letter or on our website
                <a href="#https://www.ipr.res.in/NSD2024/" class="font-medium text-blue-600 dark:text-blue-500 hover:underline"> www.ipr.res.in/NSD2024</a>
                </p>
                <p></p>

                <p className="text-gray-600 mb-4 text-justify">
                A student can participate in more than one competition also.
                </p>

                <p className="text-gray-600 mb-4 text-justify">
                Last Date of Submission: on or before 1st Jan 2024, 17:00 hrs.
                </p>

                <div className="contact-info mb-4 pb-2">
                    Theme: <strong className="text-gray-800">Science and Technology for Sustainable development</strong><br />
                </div>

                <div className="note bg-gray-100 p-4 rounded-lg border-l-4 border-red-600 mb-4">
                    <h3 className="text-lg text-red-600 mb-2">NOTE:</h3>
                    <strong className="text-gray-800">Contact Person Gmail ID:</strong><br />
                    Please provide
                    <strong className="text-gray-800"> Gmail id</strong> of the school coordinator-teacher only.<br />
                    Selection rounds are online via Google Meet on this ID only.
                </div>

                <div>
                <label className="text-gray-800 text-lg block">Email Address</label>
                <input
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  className="w-1/2 text-sm text-gray-800 border border-gray-300 mb-6 px-4 py-2 rounded-lg outline-blue-600"
                  placeholder="Enter email address"
                />
                </div>

                <button type="submit" className="w-full py-3 bg-blue-600 text-white font-bold text-lg rounded-lg hover:bg-blue-700 transition duration-300">Next</button>
            </form>
        </div>
        </div>
    );
};

export default IPRForm;
